import React, { useEffect } from 'react';
import './covid.css';
const Covid = () =>{
    const getCovidData = async () =>{
        try{
            const res =  await fetch('https://api.covid19india.org/data.json');
            const actualData = await res.json();
            console.log(actualData.statewise[0]);
         }catch(err) {
            console.log(err);
         }
    }
    
    useEffect(()=>{
        getCovidData();
    },[]);
    return(
        <>
        <section>
            <h1>LIVE</h1>
            <h2>COVID-19 CORONAVIRUS TRACKER</h2>
            <div className="container">
                    <div className="first">
                        <p className="card-name">OUR COUNTRY</p>
                        <p className="card-total">INDIA</p>
                    </div>

                    <div className="first">
                        <p className="card-name"><span>TOTAL</span> RECOVERED</p>
                        <p className="card-total">INDIA</p>
                    </div>

                    <div className="first">
                        <p className="card-name"><span>TOTAL</span>CONFIRMED</p>
                        <p className="card-total">INDIA</p>
                    </div>

            </div>

            <div className="container2">
                    <div className="first">
                        <p className="card-name"><span>TOTAL</span>DEATHS</p>
                        <p className="card-total">INDIA</p>
                    </div>

                    <div className="first">
                        <p className="card-name"><span>TOTAL</span>ACTIVE</p>
                        <p className="card-total">INDIA</p>
                    </div>

                    <div className="first">
                        <p className="card-name"><span>LAST</span>UPDATED</p>
                        <p className="card-total">INDIA</p>
                    </div>
        </div>
        </section>
        </>
    );
}
export default Covid;
